import React from "react";
import todoListItemSublistForm from "./todoListItemSublistForm";

import shortid from "../../node_modules/shortid";

function TodolistItem(
  item,
  swapBtn,
  sublistInputValue,
  setSublistInputValue,
  updateSublistInputValue
) {
  const id = shortid.generate();
  return (
    <li key={id}>
      {item.content ? item.content : null}
      <button
        type="button"
        name="up"
        onClick={() => {
          swapBtn("up", item.id);
        }}
      >
        UP
      </button>
      <button
        type="button"
        name="down"
        onClick={() => {
          swapBtn("down", item.id);
        }}
      >
        DOWN
      </button>
      <button
        type="button"
        name="delete"
        onClick={() => {
          swapBtn("delete", item.id);
        }}
      >
        DELETE
      </button>
      <button
        type="button"
        name="addSublist"
        onClick={(event) => {
          event.target.parentNode.append(todoListItemSublistForm());
        }}
      >
        ADD SUBLIST
      </button>
    </li>
  );
}
export default TodolistItem;
