import React, { useState } from "react";
import TodolistItem from "./todoListItem";
import shortid from "../../node_modules/shortid";

function Todo() {
  const [todoList, setTodoList] = useState([]);
  const [todoInputValue, setTodoInputValue] = useState("");

  const swapBtn = (name, id) => {

    let todoIndex = todoList.findIndex((el) => {
      if (el.id === id) {
        return el;
      } else {
        return null;
      }
    });
    console.log(todoIndex)
    // const currentTodo = todoList.find((el) => el.id === id);
    // todoList.splice(todoIndex, 1);
    // if (name === "up") {
    //    let newTodo = todoList.splice(todoIndex - 1, 0, currentTodo);
    //    setTodoList(newTodo)
    // } else if (name === "down") {
    //     let newTodo=todoList.splice(todoIndex + 1, 0, currentTodo);
    //    setTodoList(newTodo)
    // }
  };


  return (
    <div className="todoContainer">
      <form className="todoForm" onSubmit={(event)=>{
          event.preventDefault()
          setTodoList([...todoList, {
          id: shortid.generate(),
          content: todoInputValue
      }])}}>
        <input
          type="text"
          className="todoInput"
          name="addTodoInputValue"
          onChange={(e) => {
            setTodoInputValue(e.target.value);
          }}
        ></input>
        <button type="submit" className="todoButton">
          ADD task
        </button>
      </form>
      <ul className="todoList">{todoList.map((item) => TodolistItem(item,swapBtn))}</ul>
    </div>
  );
}
export default Todo;
