import React, { useState } from "react";
import TodolistItem from "./todoListItem";
import shortid from "../../node_modules/shortid";

function Todo() {
  const [todoList, setTodoList] = useState([]);
  const [todoInputValue, setTodoInputValue] = useState("");
  const swapBTN = (btnName, todoID) => {
    let allTodos = this.state.tasks;
    let todoIndex = allTodos.findIndex((el) => {
      if (el.todoID === todoID) {
        return el;
      } else {
        return null;
      }
    });
    const currentTodo = allTodos.find((el) => el.todoID === todoID);
    allTodos.splice(todoIndex, 1);
    if (btnName === "up") {
      allTodos.splice(todoIndex - 1, 0, currentTodo);
    } else if (btnName === "down") {
      allTodos.splice(todoIndex + 1, 0, currentTodo);
    }
    this.setState((prevState) => {
      return { tasks: allTodos, ...prevState };
    });
  };


  return (
    <div className="todoContainer">
      <form className="todoForm" onSubmit={(event)=>{
          event.preventDefault()
          setTodoList([...todoList, {
          id: shortid.generate(),
          content: todoInputValue
      }])}}>
        <input
          type="text"
          className="todoInput"
          name="addTodoInputValue"
          onChange={(e) => {
            setTodoInputValue(e.target.value);
          }}
        ></input>
        <button type="submit" className="todoButton">
          ADD task
        </button>
      </form>
      <ul className="todoList">{todoList.map((item) => TodolistItem(item))}</ul>
    </div>
  );
}
export default Todo;
