import React from "react";
import todoListItemSublistForm from "./todoListItemSublistForm";

import shortid from "../../node_modules/shortid";

function TodolistItem(
  item,
  swapBtn,
  updateTodoListItem,
) {
  const id = shortid.generate();
  return (
    <li key={id}>
      {item.content ? item.content : null}
      <button
        type="button"
        name="up"
        onClick={() => {
          swapBtn("up", item.id);
        }}
      >
        UP
      </button>
      <button
        type="button"
        name="down"
        onClick={() => {
          swapBtn("down", item.id);
        }}
      >
        DOWN
      </button>
      <button
        type="button"
        name="delete"
        onClick={() => {
          swapBtn("delete", item.id);
        }}
      >
        DELETE
      </button>
      <button
        type="button"
        name="addSublist"
        onClick={(event) => {
          updateTodoListItem(item.id)
        }}
      >
        ADD SUBLIST
      </button>
      {item.sublistMenu ?     <form>
      <input
        type="text"
        className="todoInput"
        name="sublistInput"
        // onChange={updateSublistInputValue}
        // value={sublistInputValue}
      ></input>
      <button type="submit" className="todoButton">
        ADD SUBLIST
      </button>
    </form>:<p>nope</p>}
    </li>
  );
}
export default TodolistItem;
