import React, { Component } from 'react'

export default class Todo extends Component {
    state = {
        addTodoInputValue: ""
    }
    changeInputValueHandler = (event)=>{
        console.log(event)
        this.setState((prevState)=>{
            return {
                ...prevState,
                [event.target.name]: event.target.value,
            }
        })
    }
    render() {
        return (
            <div className="todoContainer">
                <form className='todoForm'> 
                    <input type='text' className='todoInput' nam onChange={(event)=>{this.changeInputValueHandler(event)}} value={this.state.addTodoInputValue}></input>
                    <button type='button' className='todoButton'>ADD task</button>
                </form>

            </div>
        )
    }
}
